drop database if exists CLINICA_HFA; create database CLINICA_HFA; use CLINICA_HFA;
create table EMPLEADO( idempleado int(5),primary key(idempleado),email varchar(50),contra varchar(50));
create table ENFERMERA( idenfermera int(5),primary key(idenfermera),Dui varchar(11),nombre varchar(25),apellido varchar(25),idempleado int(5),constraint fk_EmpleEnfe foreign key(idempleado) references EMPLEADO(idempleado));
create table DOCTOR( iddoctor int(5), primary key(iddoctor), Dui varchar(11), nombre varchar(25), apellido varchar(25), idempleado int(5), foreign key (idempleado) references EMPLEADO(idempleado) );
create table ESPECIALIDAD( idespecialidad varchar(25), primary key(idespecialidad), nombre varchar(25), iddoctor int(5), constraint fk_EspeDoc foreign key (iddoctor) references DOCTOR(iddoctor) );
create table PACIENTE( Dui varchar(11), primary key(Dui), nombre varchar(25), apellido varchar(25), clave varchar(25) );
create table CITA( idecita int AUTO_INCREMENT, primary key(idecita), fecha date, hora time, idespecialidad varchar(25), constraint fk_CITAESPE foreign key (idespecialidad) references ESPECIALIDAD(idespecialidad), idempleado int(5), constraint fk_CITAEMPLE foreign key (idempleado) references EMPLEADO(idempleado), Dui varchar(11), constraint fk_CITAPAC foreign key (Dui) references PACIENTE(Dui) );
create table COMENTARIO ( idco int(5) AUTO_INCREMENT, primary key(idco), email varchar(50), nombre varchar(50), telefono int(11), mensaje text(200) );
